mapper.insert:
insert into checkList
values(
#{pNum},
#{tNum},
#{iNum},
(ifnull(
	(select max(cl.cl_num)+1 from checkList cl 
		where p_num = #{pNum} 
			and t_num =	#{tNum} 
			and i_num =	#{iNum}), 1
		)
),
#{clName}
)

mapper.update:
update checkList

	cl_name = #{clName}

where
p_num = #{pNum}
and
t_num = #{tNum}
and
i_num = #{iNum}
and
cl_num =
#{clNum}

mapper.delete:
delete from checkList
where
p_num = #{pNum}
and
t_num = #{tNum}

and
i_num = #{iNum}


and
cl_num = #{clNum}

mapper.select:
select * from checkList
where
p_num = #{pNum}
and
t_num =
#{tNum}
and
i_num = #{iNum}

mapper.insert:
insert into	checkListItem values(
#{pNum},
#{tNum},
#{iNum},
#{clNum},
(ifnull(
	(select max(ci.ci_num)+1 from checkListItem ci 
		where p_num = #{pNum} 
			and	t_num = #{tNum} 
			and i_num = #{iNum} 
			and cl_num = #{clNum}), 1
		)
),
#{ciName},
false
)

mapper.update:
