mapper.update:
update issue set i_step=#{iStep}, i_order=#{iOrder}
where
p_num=#{pNum} and
t_num=#{tNum} and
i_num=#{iNum}

update issue set i_order = #{iOrder} 
where
p_num = #{pNum} and 		
t_num = #{tNum} and
i_num = #{iNum}

mapper.delete:
delete from issue
where
p_num = #{pNum}
and
t_num = #{tNum}

and
i_num = #{iNum}

mapper.select:
select * from issue
where
p_num = #{pNum}

	and
	t_num = #{tNum}


	and
	i_num = #{iNum}

order by i_order

mapper.delete:
delete from issue_member
where
p_num = #{pNum}
and
t_num = #{tNum}
and
i_num = #{iNum}

and
m_id = #{mId}

mapper.select:
select * from issue_member
where 
p_num = #{pNum}
and
t_num = #{tNum}
and
i_num = #{iNum}

select * from comment
where
p_num = #{pNum}
and
t_num = #{tNum}
and
i_num = #{iNum}
